# -*- coding: utf-8 -*-
import base64, struct, re
bmp_data = base64.b64decode('')

def bmp_info(data):
    data = data[:30]
    info = struct.unpack('<ccIIIIIIHH', data)

    # print(re.match('DD' ,'DDDCCCCDD'))

    # if(re.match(r'\'([A-Z]{2})\'' ,str(info[0] + info[1])).group(1) != 'BM'):
    #     print('非BMP格式')
    #     return


    return {
        'width': info[6],
        'height': info[7],
        'color': info[9]
    }

# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')